import React, { Component } from 'react'
import axios from 'axios'
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import './UserProfile.css';
import logo from './logo.png';
import Signin from './Signin';

class UserProfile extends Component {

    constructor(props) {
        super(props)
       


        this.state = {
            FN: '',
            LN: '',
            mail: '',
            snum: '',
            spass: '',
            mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/signup', this.state)
            .then(response => {
                console.log(response.data.boolean)
                var mgahd = response.data.boolean;
                if (mgahd === "True") {
                    alert("Done");

                } else {
                    alert("Error");
                }
                //document.getElementById("Demo").innerHTML = mgahd;


            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {

        var mail = sessionStorage.getItem("mail");
        
        


        const useStyles = makeStyles(theme => ({
            paper: {
                marginTop: theme.spacing(8),
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            },
            avatar: {
                margin: theme.spacing(1),
                backgroundColor: theme.palette.secondary.main,
            },
            form: {
                width: '100%',
                marginTop: theme.spacing(3),
            },
            submit: {
                margin: theme.spacing(3, 0, 2),
            },
        }));

        const { FN, LN, snum, spass } = this.state
        return (

            <div>


                <section class="section about-section gray-bg" id="about">
                    <div class="container">
                        <div class="row align-items-center flex-row-reverse">
                            <div class="col-lg-6">
                                <div class="about-text go-to">
                                    <h3 class="dark-color">About Me</h3>
                                    <h6 class="theme-color lead">A Lead UX &amp; UI designer based in Canada</h6>
                                    <p>I <mark>design and develop</mark> services for customers of all sizes, specializing in creating stylish, modern websites, web services and online stores. My passion is to design digital user experiences through the bold interface and meaningful interactions.</p>
                                    <div class="row about-list">
                                        <div class="col-md-6">
                                            <div class="media">
                                                <label>Birthday</label>
                                                <p>4th april 1998</p>
                                            </div>
                                            <div class="media">
                                                <label>Age</label>
                                                <p>22 Yr</p>
                                            </div>
                                            <div class="media">
                                                <label>Residence</label>
                                                <p>Canada</p>
                                            </div>
                                            <div class="media">
                                                <label>Address</label>
                                                <p>California, USA</p>
                                            </div>
                                        </div>
                                        <div class="col-md-6">
                                            <div class="media">
                                                <label>E-mail </label>
                                                <p id="usermail">{mail}</p>
                                            </div>
                                            <div class="media">
                                                <label>Phone</label>
                                                <p>820-885-3321</p>
                                            </div>
                                            <div class="media">
                                                <label>Skype</label>
                                                <p>skype.0404</p>
                                            </div>
                                            <div class="media">
                                                <label>Freelance</label>
                                                <p>Available</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="col-lg-6">
                        <div class="about-avatar">
                        <img src={logo} alt="Logo" />
                        </div>
                    </div>
                        </div>
                        <div class="counter">
                            <div class="row">
                                <div class="col-6 col-lg-3">
                                    <div class="count-data text-center">
                                        <h6 class="count h2" data-to="500" data-speed="500">500</h6>
                                        <p class="m-0px font-w-600">Happy Clients</p>
                                    </div>
                                </div>
                                <div class="col-6 col-lg-3">
                                    <div class="count-data text-center">
                                        <h6 class="count h2" data-to="150" data-speed="150">150</h6>
                                        <p class="m-0px font-w-600">Project Completed</p>
                                    </div>
                                </div>
                                <div class="col-6 col-lg-3">
                                    <div class="count-data text-center">
                                        <h6 class="count h2" data-to="850" data-speed="850">850</h6>
                                        <p class="m-0px font-w-600">Photo Capture</p>
                                    </div>
                                </div>
                                <div class="col-6 col-lg-3">
                                    <div class="count-data text-center">
                                        <h6 class="count h2" data-to="190" data-speed="190">190</h6>
                                        <p class="m-0px font-w-600">Telephonic Talk</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>



            </div>
        )
    }
}

export default UserProfile
