import React, { Component } from 'react'
import axios from 'axios'

import { makeStyles } from '@material-ui/core/styles';

import './UserProfile.css';
import logo from './logo.png';
import Button from '@material-ui/core/Button';
import { Redirect } from 'react-router-dom';


class UserProfile extends Component {

    constructor(props) {
        super(props)

        var id = sessionStorage.getItem("id");

        this.state = {
           
            id: id,
            mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/contacts', this.state)
            .then(response => {
                console.log(response.data.contactname)
                var mgahd = response.data.boolean;
                if (mgahd === "True") {
                    var contactname = response.data.contactname;
                    var contactid = response.data.contactid;
                    window.location.replace("http://localhost:3000/Contacts")

                } else {
                    alert("Error");
                }
                //document.getElementById("Demo").innerHTML = mgahd;
                sessionStorage.setItem("contactname",contactname);
                sessionStorage.setItem("contactid",contactid);

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {

        var mail = sessionStorage.getItem("mail");
        var num = sessionStorage.getItem("num");
        var name = sessionStorage.getItem("name");
        





        const {id} = this.state
        return (


            <div>
                <div class="card text-center">
                    <div class="card-header">
                        <ul class="nav nav-pills card-header-pills">
                            <li class="nav-item">
                                <a class="nav-link active" href="/UserProfile" variant="body2">UserProfile</a>
                            </li>
                            <li class="nav-item">
                                <form class="nav-link" onSubmit={this.submitHandler}>
                                    <input type ="hidden" name="id" value={id} onChange={this.changeHandler} ></input>
                                    <Button
                                        type="submit"
                                        color="White"
                                    > Contacts
                                    </Button>
                                </form>
                            </li>
                        </ul>
                    </div>
                </div>

                <div>




                    <center>



                        <div class="col-lg-6">
                            <div class="about-avatar">
                                <img src={logo} alt="Logo" />
                            </div>
                        </div>
                        <h1 class="dark-color">User Profile</h1>

                        <h2>

                            <div class="row about-list">
                                <div class="col-md-6">

                                    <div class="media">
                                        <label>Name</label>
                                        <p>{name}</p>
                                    </div>

                                    <div class="media">
                                        <label> Phone Number </label>
                                        <p>{num}</p>
                                    </div>




                                    <div class="media">
                                        <label>E-mail </label>
                                        <p>{mail}</p>
                                    </div>



                                </div>


                            </div>

                        </h2>


                    </center >

                </div >

            </div>

        )
    }
}

export default UserProfile
