import React, { Component } from 'react'
import axios from 'axios'

import { makeStyles } from '@material-ui/core/styles';

import './UserProfile.css';
import logo from './logo.png';
import Button from '@material-ui/core/Button';
import { Redirect } from 'react-router-dom';


class Contacts extends Component {

    constructor(props) {
        super(props)

        var id = sessionStorage.getItem("id");

        this.state = {

            id: id,
            mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/contacts', this.state)
            .then(response => {

                var mgahd = response.data.boolean;
                if (mgahd === "True") {

                    window.location.replace("http://localhost:3000/UserProfile")

                } else {
                    alert("Error");
                }
                //document.getElementById("Demo").innerHTML = mgahd;

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        var contactnames = [];
        contactnames = sessionStorage.getItem("contactnames");
        var contactid = sessionStorage.getItem("contactid");


        // const items = []

        // for (const [index, value] of elements.entries()) {
        //     items.push(<li key={index}>{value}</li>)
        // }


        for (var i = 0; i <contactnames.length; i++) {

            document.getElementById("Demo").innerHTML = contactnames[i];

        }




        const { id } = this.state
        return (


            <div>
                <div class="card text-center">
                    <div class="card-header">
                        <ul class="nav nav-pills card-header-pills">
                            <li class="nav-item">
                                <a class="nav-link" href="/UserProfile" variant="body2">UserProfile</a>
                            </li>
                            <li class="nav-item">
                                <form class="nav-link active" onSubmit={this.submitHandler}>
                                    <input type="hidden" name="id" value={id} onChange={this.changeHandler} ></input>
                                    <Button
                                        type="submit"

                                    ><font color="white">Contacts</font>
                                    </Button>
                                </form>
                            </li>
                        </ul>
                    </div>
                </div>

                {/* <div>
                    {items}
                </div> */}
              



                        <p id ="Demo"></p>
                    
           

            </div>

        )
    }
}

export default Contacts
