import React, { Component } from 'react'
import axios from 'axios'

import { makeStyles } from '@material-ui/core/styles';

import './UserProfile.css';
import logo from './logo.png';


class UserProfile extends Component {

    constructor(props) {
        super(props)



        this.state = {
            FN: '',
            LN: '',
            mail: '',
            snum: '',
            spass: '',
            mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/signup', this.state)
            .then(response => {
                console.log(response.data.boolean)
                var mgahd = response.data.boolean;
                if (mgahd === "True") {
                    alert("Done");

                } else {
                    alert("Error");
                }
                //document.getElementById("Demo").innerHTML = mgahd;


            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {

        var mail = sessionStorage.getItem("mail");
        var num = sessionStorage.getItem("num");
        var name = sessionStorage.getItem("name");





        const { FN, LN, snum, spass } = this.state
        return (

            <div>




                <center>



                    <h1 class="dark-color">User Profile</h1>
<br></br>
                    <div class="about-avatar">
                            <img width = "200" height = "200" src={logo} alt="Logo" />
                        
                    </div>
                    <h2>

                        <div class="row about-list">
                            <div class="col-md-6">

                                <div class="media">
                                    <label>Name</label>
                                    <p>{name}</p>
                                </div>

                                <div class="media">
                                    <label> Phone Number </label>
                                    <p>{num}</p>
                                </div>




                                <div class="media">
                                    <label>E-mail </label>
                                    <p>{mail}</p>
                                </div>



                            </div>


                        </div>

                    </h2>


                </center >

            </div >

        )
    }
}

export default UserProfile
