import React, { Component } from 'react'
import axios from 'axios'
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import './UserProfile.css';
import logo from './logo.png';
import Signin from './Signin';

class UserProfile extends Component {

    constructor(props) {
        super(props)



        this.state = {
            FN: '',
            LN: '',
            mail: '',
            snum: '',
            spass: '',
            mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/signup', this.state)
            .then(response => {
                console.log(response.data.boolean)
                var mgahd = response.data.boolean;
                if (mgahd === "True") {
                    alert("Done");

                } else {
                    alert("Error");
                }
                //document.getElementById("Demo").innerHTML = mgahd;


            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {

        var mail = sessionStorage.getItem("mail");
        var num = sessionStorage.getItem("num");
        var name = sessionStorage.getItem("name");




        const useStyles = makeStyles(theme => ({
            paper: {
                marginTop: theme.spacing(8),
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            },
            avatar: {
                margin: theme.spacing(1),
                backgroundColor: theme.palette.secondary.main,
            },
            form: {
                width: '100%',
                marginTop: theme.spacing(3),
            },
            submit: {
                margin: theme.spacing(3, 0, 2),
            },
        }));

        const { FN, LN, snum, spass } = this.state
        return (

            <div>


                <section class="section about-section gray-bg" id="about">
                    <div class="container">
                        <div class="row align-items-center flex-row-reverse">
                            <div class="col-lg-6">
                                <div class="about-text go-to">
                                    <h3 class="dark-color">User Profile</h3>
                                    <div class="row about-list">
                                        <div class="col-md-6">
                                            <div class="media">
                                                <label>Name</label>
                                                <p>{name}</p>
                                            </div>

                                            <div class="media">
                                                <label>Phone Number</label>
                                                <p>{num}</p>
                                            </div>




                                            <div class="media">
                                                <label>E-mail </label>
                                                <p>{mail}</p>
                                            </div>



                                        </div>


                                    </div>
                                </div>
                            </div>
                        </div>
                        <div class="col-lg-6">
                            <div class="about-avatar">
                                <img src={logo} alt="Logo" />
                            </div>
                        </div>
                    </div>

                </section>



            </div>
        )
    }
}

export default UserProfile
