import React, { Component } from 'react'
import axios from 'axios'
import './UserProfile.css';
import Button from '@material-ui/core/Button';
import logo from './logo.png';



class Contacts extends Component {

    constructor(props) {
        super(props)

        var sender = sessionStorage.getItem("id");
        var Arr = [];

        this.state = {

            sender: sender,
            Arr: Arr
            // mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/chathistory', this.state)
            .then(response => {
                var fis = response.data;
                var mgahd = response.data.boolean;
                if (mgahd === "True") {
                    // var msg = response.data.msg;
                    console.log(mgahd)
                    console.log(fis)

                 //   window.location.replace("http://localhost:3000/UserProfile")

                } else {
                    alert("Error");
                      console.log(mgahd)
                }
                //document.getElementById("Demo").innerHTML = mgahd;

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        var contactnames = JSON.parse(window.localStorage.getItem("contactnames")); // Retrieving
        // var id = sessionStorage.getItem("id");

        var contactids = JSON.parse(window.localStorage.getItem("contactids")); // Retrieving



        // const items = []

        // for (const [index, value] of elements.entries()) {
        //     items.push(<li key={index}>{value}</li>)

        for (var i = 0; i < 3; i++) {
            Arr.push(contactnames[i])

        }



        const { sender, Arr } = this.state
        
        return (


            <div>
                <div class="card text-center">
                    <div class="card-header">
                        <ul class="nav nav-pills card-header-pills">
                            <li class="nav-item">
                                <a class="nav-link" href="/UserProfile" variant="body2">UserProfile</a>
                            </li>
                            <li class="nav-item">
                            <a class="nav-link active" href="/Contacts" variant="body2">Contacts</a>

                            </li>
                        </ul>
                    </div>
                </div>

                {/* <div>
                    {items}
                </div> */}

                <div>








                    <div class="col-lg-6">
                        <div class="about-avatar">
                            <img src={logo} alt="Logo" />
                        </div>
                    </div>
                    <h1 class="dark-color">Contacts</h1>
                    <h2>
                        <form  class="Right" onSubmit={this.submitHandler}>
                            <input name="sender" type="hidden" value={sender} onChange={this.changeHandler}></input>
                            <select name="reciver">
                                {contactids.map((value, index) => {
                                    return <option value={value}>{Arr[index]}{value}</option>
                                })}
                            </select>
                            <br></br>
                            <Button
                                type="submit"
                                
                                
                            >
                            select </Button>
                        </form>


                        <div class="row about-list">
                            <div class="col-md-6">




                                <ul>
                                    {contactnames.map((x, i) => {
                                        return <li >{x}</li>
                                    })}
                                </ul>





                                {/* {contactids.map((value, index) => {
                                            return <form onSubmit={this.submitHandler}>
                                                <li key={index}>{value}</li>
                                                <input type= "text">{contactids[index]}</input>
                                                <Button
                                                    type="submit"
                                                > Send
                                                </Button>
                                            </form>
                                        })} */}



                            </div>


                        </div>

                    </h2>



                </div >








            </div>


        )
    }
}

export default Contacts
