import React, { Component } from 'react'
import axios from 'axios'
import './UserProfile.css';
// import Button from '@material-ui/core/Button';
import logo from './logo.png';



class ChatHistory extends Component {



    constructor(props) {
        super(props)

        var sender = sessionStorage.getItem("id");
        var x = 2;

        this.state = {

            sender: sender,
            x: x
            // mgahd: 'False'
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios
            .post('https://maimoji.herokuapp.com/api/chathistory', this.state)
            .then(response => {

                var mgahd = response.data.boolean;
                var rec = response.data.reciever;
                //  var loops = response.data.loop;
                if (mgahd === "True" && rec !== "") {
                    var messages = []
                    var dates = []
                    var senders = []
                    var recivers = []
                    console.log(response.data)
                    var loops = response.data.looping;
                    for (var i = 0; i < loops; i++) {
                        const message = response.data.msg[i];
                        const date = response.data.date[i];
                        const sender = response.data.sender[i];
                        const reciver = response.data.recivers[i];
                        messages.push(message)
                        dates.push(date)
                        senders.push(sender)
                        recivers.push(reciver)

                    }
                    window.localStorage.setItem("loops", JSON.stringify(loops)); // Saving
                    window.localStorage.setItem("messages", JSON.stringify(messages)); // Saving
                    window.localStorage.setItem("dates", JSON.stringify(dates)); // Saving
                    window.localStorage.setItem("senders", JSON.stringify(senders)); // Saving
                    window.localStorage.setItem("recivers", JSON.stringify(recivers)); // Saving

                    //   window.location.replace("http://localhost:3000/chathistory")

                } else {
                    alert("Error");
                    console.log(mgahd)
                }
                //document.getElementById("Demo").innerHTML = mgahd;

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        var messages = JSON.parse(window.localStorage.getItem("messages")); // Retrieving
        // var id = sessionStorage.getItem("id");
        var loops = JSON.parse(window.localStorage.getItem("loops")); // Retrieving
        var dates = JSON.parse(window.localStorage.getItem("dates")); // Retrieving
        var senders = JSON.parse(window.localStorage.getItem("senders")); // Retrieving
        var recivers = JSON.parse(window.localStorage.getItem("recivers")); // Retrieving



        // const items = []

        // for (const [index, value] of elements.entries()) {
        //     items.push(<li key={index}>{value}</li>)

        var sender1 = [];
        var date1 = [];
        var reciever1 = [];
        var message1 = [];
        for (var i = 0; i < loops; i++) {
            sender1.push(senders[i])
            date1.push(dates[i])
            reciever1.push(recivers[i])
            message1.push(messages[i])


        }



        const { sender } = this.state

        return (


            <div>
                {/* <div class="card text-center">
                    <div class="card-header">
                        <ul class="nav nav-pills card-header-pills">
                            <li class="nav-item">
                                <a class="nav-link" href="/UserProfile" variant="body2">UserProfile</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" href="/Contacts" variant="body2">Contacts</a>

                            </li>
                        </ul>
                    </div>
                </div> */}

                {/* <div>
                    {items}
                </div> */}

                <div>

                    <div class="col-lg-6">
                        <div class="about-avatar">
                            <img src={logo} alt="Logo" />
                        </div>
                    </div>
                    <h1 class="dark-color">Chat History</h1>
                    <h2>

                        <div class="row about-list">
                            <div class="col-md-6">

                                <input name="sender" type="hidden" value={sender} onChange={this.changeHandler}></input>
                                {dates.map((value, index) => {
                                    var z = value;
                                    console.log(z)
                                    // return <option value={z}>{Arr[index]}</option>


                                    return <div class="card text-white bg-info mb-3" >
                                        <div class="card-header">{date1[index]}</div>
                                        <div class="card-body">
                                            <h3 class="card-title">{sender1[index]}</h3>

                                            <p class="card-text">{message1[index]}</p>
                                        </div>
                                    </div>

                                })}

                                <form class="Right" onSubmit={this.submitHandler}>
                                    <div class="fixed-bottom">
                                        <div class="input-group mb-3">
                                            <input type="text" class="form-control" placeholder="Recipient's username" aria-label="Recipient's username" aria-describedby="button-addon2"></input>
                                                <div class="input-group-append">
                                                    <button class="btn btn-outline-secondary" type="button" id="button-addon2">Button</button>
                                                </div>
                                                  </div>

                                        </div>
                                        <br></br>
                                   
                                </form>

                            </div>


                            </div>

                    </h2>



                </div >








                </div>


        )
    }
}

export default ChatHistory
